import React, {useState, useEffect} from 'react'
import BackendErrorMessages from 'components/BackendErrorMessages'

const ArticleForm = ({onSubmit, errors, initialValue}) => {
  const [title, setTitle] = useState('')
  const [body, setBody] = useState('')
  const [description, setDescription] = useState('')
  const [tagList, setTagList] = useState('')

  useEffect(() => {
    if (!initialValue) {
      return
    }
    setTitle(initialValue.title)
    setDescription(initialValue.description)
    setBody(initialValue.body)
    setTagList(initialValue.tagList.join(','))
  }, [initialValue])
  const handleSubmit = event => {
    event.preventDefault()
    onSubmit({title, body, description, tagList})

  }

  return (
    <div className={'editor-page'}>
      <div className="container page">
        <div className="row">
          <div className="col-md-10 offset-md-1 col-xs-12">
            {errors && <BackendErrorMessages backendErrors={errors}/>}
            <form onSubmit={handleSubmit}>
              <fieldset>
                <fieldset className={'form-group'}>
                  <input
                    type="text"
                    className={'form-control form-control-lg'}
                    placeholder={'Article title'}
                    value={title}
                    onChange={e => setTitle(e.target.value)}
                  />
                </fieldset>
                <fieldset className={'form-group'}>
                  <input
                    type="text"
                    className={'form-control form-control-lg'}
                    placeholder={'Article about'}
                    value={description}
                    onChange={e => setDescription(e.target.value)}
                  />
                </fieldset>
                <fieldset className={'form-group'}>
                  <textarea
                    className={'form-control form-control-lg'}
                    rows={8}
                    placeholder={'Article body'}
                    value={body}
                    onChange={e => setBody(e.target.value)}
                  />
                </fieldset>
                <fieldset className={'form-group'}>
                  <input
                    type="text"
                    className={'form-control form-control-lg'}
                    placeholder={'Article tags'}
                    value={tagList}
                    onChange={e => setTagList(e.target.value)}
                  />
                </fieldset>
                <fieldset className={'form-group'}>
                  <button
                    type={'submit'}
                    className={'btn btn-lg pull-xs-right btn-primary'}
                  >
                    Publish article
                  </button>
                </fieldset>
              </fieldset>
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}

export default ArticleForm
