import React from 'react'
import {Link} from 'react-router-dom'
import TagList from 'components/TagList'

const Feed = ({posts, currentPage}) => {
  if (!posts || posts.length === 0) {
    return <div>none posts</div>
  }

  return (
    <div>
      {posts[currentPage - 1].map((post, index) => (
        <div className={'article-preview'} key={index}>
          <div className={'article-meta'}>
            <Link to={`/profiles/${post.username}`}>
              <img src={post.image} alt=""/>
            </Link>
            <div className={'info'}>
              <Link to={`/profiles/${post.username}`} className={'author'}>
                {post.username}
              </Link>
              <span className={'date'}>
                {new Date(post.createdAt).toLocaleDateString('ru') + ' / '}
                {new Date(post.createdAt).toLocaleTimeString('ru')}
              </span>
            </div>
          </div>
          <Link to={`/articles/${post.id}`} className={'preview-link'}>
            <h1>{post.title}</h1>
            <p>{post.description}</p>
            <span>Read more...</span>
            <TagList tags={post.tagList}/>
          </Link>
        </div>
      ))}
    </div>
  )
}

export default Feed
