import React from 'react'
import {Link} from 'react-router-dom'

const Feed = ({posts}) => {
  return (
    <div>
      {posts.map((post, index) => (
        <div className={'article-preview'} key={index}>
          <div className={'article-meta'}>
            <Link to={`/profiles/${post.username}`}>
              <img src={post.image} alt=""/>
            </Link>
            <div className={'info'}>
              <Link to={`/profiles/${post.username}`} className={'author'}>
                {post.username}
              </Link>
              <span className={'date'}>{post.createdAt}</span>
            </div>
          </div>
          <Link to={`/articles/${post.slug}`} className={'preview-link'}>
            <h1>{post.title}</h1>
            <p>{post.description}</p>
            <span>Read more...</span>
            <ul className={'tag-list'}>
              {post.tagList.split(',').map(tag => (
                <li key={tag} className={'tag-default tag-pill tag-outline'}>{tag.trim()}</li>
              ))}
            </ul>
          </Link>
        </div>
      ))}
    </div>
  )
}

export default Feed