import React, {useContext, useEffect, useState} from 'react'
import {NavLink} from 'react-router-dom'
import {CurrentUserContext} from 'contexts/currentUser'

const TopBar = () => {
  const [currentUserState] = useContext(CurrentUserContext)
  const [username, setUsername] = useState('')
  const [photoURL, setPhotoURL] = useState('')

  useEffect(() => {
    if (!currentUserState.currentUser) return
    setUsername(currentUserState.currentUser.username)
    setPhotoURL(currentUserState.currentUser.photoURL)
  }, [currentUserState.currentUser])

  return (
    <nav className="navbar navbar-light">
      <div className="container">
        <NavLink to={'/'} className="navbar-brand">Medium</NavLink>
        <ul className="nav navbar-nav pull-xs-right">
          <li className="nav-item">
            <NavLink to={'/'} className="nav-link" exact>Home</NavLink>
          </li>
          {!currentUserState.isLoggedIn && (
            <>
              <li className="nav-item">
                <NavLink to={'/login'} className="nav-link">Sign in</NavLink>
              </li>
              <li className="nav-item">
                <NavLink to={'/register'} className="nav-link">Sign up</NavLink>
              </li>
            </>
          )}
          {currentUserState.isLoggedIn && (
            <>
              <li className="nav-item">
                <NavLink to={'/articles/new'} className="nav-link">
                  <i className="ion-compose"/>
                  &nbsp; New Post
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink to={'/settings'} className="nav-link">
                  <i className="ion-gear-a"/>
                  &nbsp; Settings
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink to={`/profiles/${currentUserState.currentUser.uid}/`} className="nav-link">
                  <img className={'user-pic'} src={photoURL} alt=""/>
                  &nbsp; {username}
                </NavLink>
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  )
}

export default TopBar