import React, {useEffect} from 'react'
import useFirebase from 'hooks/useFirebase'
import React, {useEffect, useContext, useState} from 'react'
import {Link, Redirect} from 'react-router-dom'
import Loading from 'components/Loading'
import ErrorMessage from 'components/ErrorMessage'
import TagList from 'components/TagList'
import {CurrentUserContext} from 'contexts/currentUser'

const Article = ({match}) => {
  const slug = match.params.slug
  const apiUrl = `/articles/${slug}`
  const [{response: articleResponse, error: articleError, articleIsLoading}, doFetch] = useFetch(apiUrl)
  const [{response: deleteArticleResponse}, doDeleteArticle] = useFetch(apiUrl)
  const [currentUserState] = useContext(CurrentUserContext)
  const [isSuccessFullSubmit, setIsSuccessFullSubmit] = useState(false)

  const isAuthor = () => {
    if (!articleResponse || !currentUserState.isLoggedIn) return

    return articleResponse.article.author.username === currentUserState.currentUser.username

  }

  const deleteArticle = () => {
    doDeleteArticle({
      method: 'delete'
    })
  }

  useEffect(() => {
    dispatch({
      type: 'GET_ONE_POST',
      payload: slug
    })
    dbGet()

  }, [slug, dbGet])

  let post
  if (response) {
    post = response.posts[0][0]
  }

  return (
    <div className={'article-page'}>
      <div className="banner">
        {!isLoading && response && (
          <div className={'container'}>
            <h1>{post.title}</h1>
            <div className={'article-meta'}>
              <Link to={`/profiles/${post.username}`}>
                <img src={post.image} alt=""/>
              </Link>
              <div className="info">
                <Link to={`/profiles/${post.username}`}>
                  {post.username}
                </Link>
                <span className="date">{new Date(post.createdAt).toDateString()}</span>
              </div>
              {isAuthor() && (
                <span>
                  <Link
                    className={'btn btn-outline-secondary btn-sm'}
                    to={`/articles/${article.slug}/edit`}
                  ><i className={'ion-edit'}/>
                  Edit Article
                  </Link>
                  <button
                    className={'btn btn-outline-danger btn-sm'}
                    onClick={deleteArticle}
                  >
                    <i className={'ion-trash-a'}/>
                    Delete article
                  </button>
                </span>
              )}
            </div>
          </div>
        )}
      </div>
      <div className="container page">
        {isLoading && <Loading/>}
        {error && <ErrorMessage/>}
        {!isLoading && response && (
          <div className={'row article-content'}>
            <div className="col-xs-12">
              <div>
                <p>{post.description}</p>
              </div>
              <TagList tags={post.tagList}/>
            </div>
          </div>
        )}
        <hr/>
      </div>
    </div>
  )
}

export default Article
