import React, {useEffect} from 'react'
import useFirebase from 'hooks/useFirebase'
import Feed from 'components/Feed'
import Pagination from 'components/Pagination'
import PopularTags from 'components/PopularTags'
import Loading from 'components/Loading'
import ErrorMessage from 'components/ErrorMessage'
import FeedToggler from 'components/FeedToggler'
import {getPaginator, limit} from 'utils'
import useAuthChecker from 'hooks/useAuthChecker'

const YourFeed = ({location, match}) => {
  const {url} = match
  const {currentPage} = getPaginator(location.search)
  const [{response, isLoading, error}, dbGet, dispatch] = useFirebase()
  const [authUser] = useAuthChecker()

  useEffect(() => {
    if (!authUser) return
    dispatch({
      type: 'YOR_FEED',
      payload: authUser.uid
    })
    dbGet()
  }, [dbGet, authUser])

  return (
    <div className={'home-page'}>
      <div className={'banner'}>
        <div className={'container'}>
          <h1>Medium clone</h1>
          <p>A place to share knowledge</p>
        </div>
      </div>
      <div className={'container page'}>
        <div className={'row'}>
          <div className={'col-xs-12 col-md-9'}>
            <FeedToggler tagName={''}/>
            {isLoading && <Loading/>}
            {error && <ErrorMessage/>}
            {!isLoading && response && (
              (
                <>
                  <Feed posts={response.posts} currentPage={currentPage}/>
                  <Pagination total={response.countLength} limit={limit} url={url} currentPage={currentPage}/>
                </>
              )
            )}
          </div>
          <div className={'col-xs-12 col-md-3'}>
            <PopularTags/>
          </div>
        </div>
      </div>
    </div>
  )
}

export default YourFeed