export default (state, action) => {
  switch (action.type) {
    case 'YOR_FEED':
      return {
        ...state,
        filter: 'TAGS_POST',
        url: '/posts',
        orderBy: 'createdAt',
        userFilter: {
          fieldPath: 'userUid',
          opStr: '==',
          value: action.payload
        }
      }
    case 'TAGS_POST':
      return {
        ...state,
        filter: 'TAGS_POST',
        url: '/posts',
        orderBy: 'createdAt',
        userFilter: {
          fieldPath: 'tagList',
          opStr: 'array-contains',
          value: action.payload
        }
      }
    case 'TAGS':
      return {
        ...state,
        filter: 'TAGS',
        url: '/tags',
        orderBy: 'popularity'
      }
    case 'ALL_POSTS':
      return {
        ...state,
        filter: 'ALL_POSTS',
        url: '/posts',
        orderBy: 'createdAt'
      }
    default:
      return state
  }
}
